import React, {useState} from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { useHistory } from 'react-router-dom';
import { Link, useLocation } from 'react-router-dom';
import axios from 'axios';


import { FormControl, FormLabel, RadioGroup, Radio } from '@material-ui/core';


const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  error: {
    color: "red"
  },
  astext: {
    background: "none",
    border:"none",
    margin:0,
    padding:0,
    cursor: "pointer"
  }
}));

export default function SignUp(props) {
  const classes = useStyles();

  const history = useHistory();

  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [repeatedPassword, setRepeatedPassword] = useState("")
  const [errorMessage, setErrorMessage] = useState("")

  const handleClick = () => {
    if (email == "" || password == "") {
        setErrorMessage("Please enter details")
        return
    }
    axios.post('https://back-ecommerce01.herokuapp.com/auth/register', {
        name: email,
        password: password
    }).then(res => {

        axios.post('https://back-ecommerce01.herokuapp.com/auth/login', {
            name: email,
            password: password
        }).then(res => {
            props.setToken(res.data)
            history.push('/ecomm')
        }).catch(function (error) {
            setErrorMessage("Problem during connexion")
        });
    }).catch(function (error) {
        setErrorMessage("Sign Up refused: use another name")
    });
  }

  const handleGoSignIn = () => {
    history.push('/ecomm/signin')
  }


  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Sign Up
        </Typography>
        <form className={classes.form} noValidate>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email Address"
            name="email"
            autoComplete="email"
            value={email} onChange={(event) => setEmail(event.target.value)}
            autoFocus
          />
          <TextField
            value={password} onChange={(event) => setPassword(event.target.value)}
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
          />

          <TextField
            value={repeatedPassword} onChange={(event) => setRepeatedPassword(event.target.value)}
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="Repeat Password"
            label="Repeat Password"
            type="password"
            autoComplete="current-password"
          />

        <FormControl component="fieldset">
          <FormLabel component="legend">Gender</FormLabel>
          <RadioGroup aria-label="gender" name="gender1" value={value} onChange={handleChange}>
            <FormControlLabel value="Informational Technology" control={<Radio />} label="Female" />
            <FormControlLabel value="Business Intelligence" control={<Radio />} label="Male" />
            <FormControlLabel value="Data Science" disabled control={<Radio />} label="(Disabled option)" />
            <FormControlLabel value="Digital Economics" control={<Radio />} label="Other" />
            <FormControlLabel value="biological Computing" disabled control={<Radio />} label="(Disabled option)" />
            <FormControlLabel value="E-commerce" disabled control={<Radio />} label="(Disabled option)" />
          </RadioGroup>
        </FormControl>

          <Button
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
            onClick={handleClick}
          >
            Sign Up
          </Button>
          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Forgot password?
              </Link>
            </Grid>
            <Grid item>
              <a className={classes.astext} onClick={handleGoSignIn} variant="body2">
                {"Don't have an account? Sign Up"}
              </a>
            </Grid>
          </Grid>
        </form>
        <p className={classes.error}>{errorMessage}</p> 
      </div>
    </Container>
  );
}
