import React, {useState} from 'react';
import Grid from '@material-ui/core/Grid';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

import Product from './Product/Product';
import useStyles from './styles';

const Products = ({onAddToCart, token }) => {
  const classes = useStyles();
  const history = useHistory();

  const [products, setProducts] = useState([])

  if (!products.length) return <p>Loading...</p>;

  if (token == "") {
    history.push('/ecomm/signin')
  }

  if (products == []) {
    axios.get('https://back-ecommerce01.herokuapp.com/home/courses', {
      headers: {
        'Authorization': token
      }}).then(res => {
          setProducts(res)
          console.log("=========== Products ===========")
          console.log(products)
    }).catch(function (error) {
      console.log("=======> problem get products")
    });
  }

  var display = <p>Loading...</p>;

  if (products != []) {
    display = <Product product={products} onAddToCart={onAddToCart} />
  }



  return (
    <main className={classes.content}>
      <div className={classes.toolbar} />
      <Grid container justify="center" spacing={4}>
        {products.map((product) => (
          <Grid key={product.id} item xs={12} sm={6} md={4} lg={3}>
            {display}
          </Grid>
        ))}
      </Grid>
    </main>
  );
};

export default Products;
